// Queue a WhatsApp message to be sent
const item = $input.item;
const body = item.json.body;

// Expected input:
// { "to": "+491234567890", "body": "Message text" }

const to = body.to;
const messageBody = body.body || body.message || body.text;

if (!to || !messageBody) {
  throw new Error('Missing required fields: to, body');
}

// Format phone number (remove + and spaces, ensure @c.us suffix)
let formattedTo = to.replace(/[+\s]/g, '');
if (!formattedTo.includes('@c.us')) {
  formattedTo = formattedTo + '@c.us';
}

const queuedMessage = {
  id: 'msg-' + Date.now() + '-' + Math.random().toString(36).substr(2, 9),
  to: formattedTo,
  body: messageBody,
  queuedAt: new Date().toISOString(),
  status: 'pending'
};

console.log('[Queue] Added message:', queuedMessage.id, 'to', to);

// Write to file (simple queue)
const fs = require('fs');
const queueFile = '/tmp/whatsapp-queue.json';

let queue = [];
try {
  if (fs.existsSync(queueFile)) {
    const data = fs.readFileSync(queueFile, 'utf8');
    queue = JSON.parse(data);
  }
} catch (err) {
  console.error('Failed to read queue file:', err.message);
  queue = [];
}

queue.push(queuedMessage);

try {
  fs.writeFileSync(queueFile, JSON.stringify(queue, null, 2));
} catch (err) {
  console.error('Failed to write queue file:', err.message);
  throw new Error('Failed to queue message');
}

return {
  json: {
    success: true,
    messageId: queuedMessage.id,
    queuedAt: queuedMessage.queuedAt,
    queueLength: queue.length
  }
};
