// Return pending WhatsApp messages and clear queue
const fs = require('fs');
const queueFile = '/tmp/whatsapp-queue.json';

let pendingMessages = [];

try {
  if (fs.existsSync(queueFile)) {
    const data = fs.readFileSync(queueFile, 'utf8');
    const queue = JSON.parse(data);

    // Get all pending messages
    pendingMessages = queue.filter(msg => msg.status === 'pending').map(msg => ({
      to: msg.to,
      body: msg.body,
      id: msg.id
    }));

    if (pendingMessages.length > 0) {
      console.log(`[Poll] Returning ${pendingMessages.length} pending message(s)`);

      // Clear the queue after returning messages
      fs.writeFileSync(queueFile, JSON.stringify([]));
    }
  }
} catch (err) {
  console.error('[Poll] Error reading queue:', err.message);
  // Return empty on error
}

// Return format expected by bridge
return {
  json: {
    messages: pendingMessages
  }
};
