// Parse queue and prepare response WITH MEDIA SUPPORT
let pendingMessages = [];

const fileInput = $('Read Queue File').first();

if (fileInput && fileInput.json && fileInput.binary && fileInput.binary.data) {
  try {
    const fileContent = fileInput.binary.data.data;
    let bufferObj = Buffer.from(fileContent, 'base64');
    const queue = JSON.parse(bufferObj.toString('utf8'));

    if (Array.isArray(queue)) {
      // Filter pending messages and format for bridge
      pendingMessages = queue
        .filter(msg => msg.status === 'pending')
        .map(msg => {
          const msgData = {
            to: msg.to,
            body: msg.body,
            id: msg.id
          };

          // IMPORTANT: Include media if present
          if (msg.media && msg.media.data && msg.media.mimetype) {
            msgData.media = {
              data: msg.media.data,
              mimetype: msg.media.mimetype,
              filename: msg.media.filename || 'file'
            };
          }

          return msgData;
        });

      if (pendingMessages.length > 0) {
        const mediaCount = pendingMessages.filter(m => m.media).length;
        console.log(`[Poll] Returning ${pendingMessages.length} message(s), ${mediaCount} with media`);
      }
    }
  } catch (err) {
    console.error('[Poll] Failed to parse queue:', err.message);
  }
} else {
  console.log('[Poll] Queue file empty or not found');
}

const emptyQueueBase64 = Buffer.from(JSON.stringify([])).toString('base64');

// Return empty queue data to clear file
return {
  json: {
    messages: pendingMessages,
    shouldClear: pendingMessages.length > 0,
    clearedData: JSON.stringify([])
  },
  binary: {
    data: {
      data: emptyQueueBase64,
      mimeType: 'application/json',
      fileName: 'whatsapp-queue.json'
    }
  }
};
